import React, { useState } from 'react'
import Tesseract from 'tesseract.js'; // Import tesseract.js

export default function ImageToText(props) {
  const [text, setText] = useState("")

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    
    if (file) {
        const reader = new FileReader();
        reader.onload = async () => {
            const imageBase64 = reader.result;
            const result = await Tesseract.recognize(imageBase64, 'eng');
            const extractedText = result.data.text;
            
            // Set the extracted text in the textarea
            setText(extractedText);
        };
        reader.readAsDataURL(file);
    }
  };
  
  return (
    <div className="container">
    <label htmlFor="myBox" className="form-label" style={{ fontSize: '24px', fontWeight: 'bold' }}>{props.heading}</label>
    <div className="container">

        <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
            />
    </div>

</div>
  )
}
