import { useState } from 'react';
import './App.css';

import * as React from "react";
import * as ReactDOM from "react-dom/client";
import "./index.css";

import About from './Components/About';
import Navbar from './Components/Navbar';
import TextForm from './Components/TextForm';
import Alert from './Components/Alert';
import {
  createBrowserRouter,
  RouterProvider,
} from "react-router-dom";

function App() {
  const [mode, setMode]=useState('dark');
  const [alert, setAlert]=useState(null);

  const showAlert = (message , type) => {
     setAlert({
      msg:message,
      type:type
     })
     setTimeout(() => {
         setAlert(null)
     }, 3000);
  }
  const toggleMode = () => {
     if(mode === 'dark'){
      setMode('light')
      document.body.style.backgroundColor = 'white';
      document.body.style.color = 'black';
      showAlert('Light mode has been enabled', 'success')
      // document.title = 'ChtutoPack - Light Mode'
     }
     else{
      setMode('dark')
      document.body.style.backgroundColor = '#483D8B'
      document.body.style.color = 'white';
      showAlert('dark mode has been enabled', 'success')
      // document.title = 'ChtutoPack - dark Mode'

     }
  }
  const router = createBrowserRouter([
    {
      path: "/",
      element: <TextForm heading="Enter Your Text Below"  alert={showAlert}/>,
    },
    {
      path: "/",
      element: <About />,
    },
  ]);
  ReactDOM.createRoot(document.getElementById("root")).render(
    <React.StrictMode>
      <RouterProvider router={router} />
    </React.StrictMode>
  );


  return (
    <>
    <Navbar title="ChutoPack" mode={mode} toggleMode={toggleMode}/>
    <Alert alert={alert}/>
    <div className="container my-3">


    </div>

    </>
  );
}

export default App;
